import React from "react";
import "./button.scss";

const ChooseButton = ({text, choice, isActive, onClick}) => {

    let activeStyle = {};
    if(isActive) {
        activeStyle = {
            border: "1px solid red"
        }
    }

    return (
        <button className={"btn-primary"} onClick={() => onClick(choice)} >{text}</button>
    )
}
export default ChooseButton;
