import React, { useState } from 'react';
import { CustomTitle, ErrorInfo } from '../../Layouts';

const ContactUs = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [contactUs, setContactUs] = useState({
    name: '',
    email: '',
    message: '',
    });
    const [errors, setErrors] = useState({
    name: '',
    email: '',
    message: '',
    });
    const [status, setStatus] = useState('');
    const [error, setError] = useState('');

    const handleChange = e => {
    if (e.target.name === 'name') {
        setName(e.target.value);
    } else if (e.target.name === 'email') {
        setEmail(e.target.value);
    } else if (e.target.name === 'message') {
        setMessage(e.target.value);
    }
    };

    const validate = () => {
    let isValid = true;
    const nameRegex = /^[a-zA-Z]{1,50}$/;
    const emailRegex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    const messageRegex = /^.{120,1000}$/;

    if (!nameRegex.test(name) || name.length <= 0) {
        isValid = false;
        errors.name = 'Podane imię jest nieprawidłowe!';
    } else {
        isValid = true;
        errors.name = '';
        contactUs.name = name;
    }

    if (!emailRegex.test(email)) {
        isValid = false;
        errors.email = 'Podany email jest nieprawidłowy!';
    } else {
        isValid = true;
        errors.email = '';
        contactUs.email = email;
    }

    if (!messageRegex.test(message)) {
        isValid = false;
        errors.message = 'Wiadomość musi mieć conajmniej 120 znaków!';
    } else {
        isValid = true;
        errors.message = '';
        contactUs.message = message;
    }

    setErrors({ ...errors });

    setContactUs({ ...contactUs });

    return isValid;
    };

    const sendForm = (name, email, message) => {
    const url = `https://fer-api.coderslab.pl/v1/portfolio/contact`;
    fetch(url, {
        method: 'POST',
        headers: {
        'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, message }),
    })
        .then(resp => resp.json())
        .then(data => setStatus(data.status))
        .catch(error => setError(error));
    };

    const handleSubmit = e => {
    e.preventDefault();
    if (validate()) {
        sendForm(name, email, message);
        setName('');
        setEmail('');
        setMessage('');
    } else {
        return errors;
    }
    };
    return (
        <form className="t-contact--main-form" noValidate onSubmit={handleSubmit} >
            <div className="succes-send">{succes}</div>
            <div className="half-page">
                <label>Wpisz swoje imię</label>
                <input  type="text" 
                        name={"name"} 
                        value={name} 
                        placeholder={"Podaj imię"} 
                        className="input-text"
                        onChange={e => setName(e.target.value)}/>
                <div className="error-name">{errName}</div>
            </div>
            <div className="half-page">
                <label>Wpisz swój email</label>
                <input  type="email" 
                        name="email" 
                        value={email} 
                        placeholder="adres@email" 
                        className="input-text"
                        onChange={e => setEmail(e.target.value)}/>
                <div className={"error-email"}>{errEmail}</div>
            </div>
            <div className="textarea">
                <label>Wpisz swoją wiadomość</label>
                <textarea   name="message" 
                            value={message}
                            placeholder={"Lorem ipsum dolor sit amet, consectetur adipisicing elit. Blanditiis, repellat?"}
                            className="input-text" 
                            rows={"5"} 
                            onChange={e => setMessage(e.target.value)}/>
                <div className="error-message">{errMessage}</div>
            </div>
            <div>
                <input type="submit" value={"Wyślij"} className={"button"}/>
            </div>
        </form>
    )
}

export default ContactUsInputs;
