import React, {useState} from "react";

const ContactUsInputs = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [errName, setErrName] = useState([]);
    const [errEmail, setErrEmail] = useState([]);
    const [errMessage, setErrMessage] = useState([]);
    const [succes, setSucces] = useState('');

    const handleSubmit = e => {
        e.preventDefault();

        let isValid = true;
// name validate
        if (name.length < 3) {
            setErrName('Imię jest za krótkie.');
            isValid = false;
        } else {
            setErrName('');
        }

// email validate
        const regEmail = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

        if (email.length < 5 || !regEmail.test(email)) {
            setErrEmail('Email jest nieprawidłowy');
            isValid = false;
        } else {
            setErrEmail('');
        }

// message validate
        if (message.length < 120) {
            setErrMessage("Wiadomość ma zawierać conajmniej 120 znaków");
            isValid = false;
        } else {
            setErrMessage('');
        }

// send or not form

        if (!isValid) {
            console.log("bledy")
        } else {
            setName('');
            setEmail('');
            setMessage('');
            setErrName('');
            setErrEmail('');
            setErrMessage('');
            setSucces("Wiadomość została wysłana. Wkrótce się skontaktujemy.")
        }
    }

return (
<form className="t-contact--main-form" noValidate onSubmit={handleSubmit} >

    <div className="succes-send">{succes}</div>
    <div className="o-row t-form--control">
    <div className="t-form__half-page">
    <div className="t-form__label">

    <label className="t-form__label">Wpisz swoje imię</label>
        <input  type="text" 
                name="name" 
                value={name} 
                placeholder="Podaj imię" 
                onChange={e => setName(e.target.value)}
                className="t-form__input"/>
    <div className=
    {errors.name ? 'error' : null} >{errName}</div>

    </div>
    </div>

    <div className="t-form__half-page">
    <div className="t-form__label">

    <label className="t-form__label">Wpisz swój email</label>
        <input  type="email" 
                name="email" 
                value={email} 
                placeholder="adres@email" 
                className="t-form__input"
                onChange={e => setEmail(e.target.value)}
                />
    <div className="t-form__span">{errEmail}</div>

    </div>
    </div>

    <div className="o-row t-form--message">
    <div className="t-form--message-page">

    <label className="t-form__label">Wpisz swoją wiadomość</label>
    <div className="t-form__input">

    <textarea   name="message" 
                value={message}
                placeholder={"Lorem ipsum dolor sit amet, consectetur adipisicing elit. Blanditiis, repellat?"}
                className="t-form__input__message1" 
                rows={"5"} 
                onChange={e => setMessage(e.target.value)}/>

            <div className="t-form__span">{errMessage}</div>
    </div>

        <div className="t-form--button">
        <input type="submit" value={"Wyślij"} className="t-form__btn"/>
        </div>


    </div>
    </div>
</div>
</form>
)
}

export default ContactUsInputs;
