import React, {useState} from "react";
import {NavLink} from "react-router-dom";

const Register = () => {

    const [displayName, setDisplayName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [errors, setErrors] = useState({
    displayName: '',
    email: '',
    password: '',
    confirmPassword: '',
    });

    const validate = () => {
        let isValid = true;
        const emailRegex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

    if (!displayName) {
        isValid = false;
        errors.displayName = 'Podane imię jest nieprawidłowe!';
    } else {
        isValid = true;
        errors.displayName = '';
    }

    if (!emailRegex.test(email)) {
        isValid = false;
        errors.email = 'Podany email jest nieprawidłowy!';
    } else {
        isValid = true;
        errors.email = '';
    }

    if (password.length < 6) {
        isValid = false;
        errors.password = 'Podane hasło jest za krótkie!';
    } else {
        isValid = true;
        errors.password = '';
    }
    if (password.length < 6) {
        isValid = false;
        errors.password = 'Podane hasło jest za krótkie!';
    } else {
        isValid = true;
        errors.password = '';
}

    if (confirmPassword !== password) {
        isValid = false;
        errors.confirmPassword = 'Podane hasła muszą być identyczne!';
    } else {
        isValid = true;
        errors.confirmPassword = '';
    }

    setErrors({ ...errors });

    return isValid;
    };

    const handleSubmit = async e => {
    e.preventDefault();

    if (validate()) {
    if (password !== confirmPassword) {
    alert('passwords dont match');
    return;
    }

    try {
    const { user } = await auth.createUserWithEmailAndPassword(
    email,
    password
    );

    await createUserProfileDocument(user, { displayName });

    history.push('/');
    setDisplayName('');
    setEmail('');
    setPassword('');
    setConfirmPassword('');
    } catch (error) {
          // console.log("err");
        }
    } else {
        return errors;
    }
    };

    const handleChange = e => {
    if (e.target.name === 'name') {
        setDisplayName(e.target.value);
    } else if (e.target.name === 'email') {
        setEmail(e.target.value);
    } else if (e.target.name === 'password') {
        setPassword(e.target.value);
    } else if (e.target.name === 'password2') {
        setConfirmPassword(e.target.value);
    }
    };



return (
<div className={"container"}>
    <div className={"login-container"}>
    <div>
    <h2>Załóź konto</h2>
    <div className={"decoration"}/>

    <form onSubmit={handleSubmit} className={"login-form"}>

    <label>Wpisz swój email</label>
    <input type="email" name={"email"} value={email} placeholder={"adres@email"} className={"input-text"}
    onChange={e => setEmail(e.target.value)}/>
    <div className={"error-email"}>{errEmail}</div>

    <label>Podaj hasło</label>
    <input type="password" name={"password"} value={password} placeholder={"Podaj hasło"}
    className={"input-text"}
    onChange={e => setPassword(e.target.value)}/>
    <div className={"error-password"}>{errPassword}</div>
    <label>Powtórz hasło</label>
    <input type="password" name={"validPassword"} value={validPassword} placeholder={"Podaj ponownie hasło"}
    className={"input-text"}
    onChange={e => setValidPassword(e.target.value)}/>
    <div className={"error-password"}>{errPassword}</div>

    <div className={"login-buttons"}>
    <input type="submit" value={"Załóż konto"} className={"button"}/><NavLink to="/logowanie" className={"button"}>Zaloguj </NavLink>
    </div>

    </form>
    </div>

    </div>
        </div>
    )
};

export default Register;
