import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { auth } from './../firebase/firebase.utils';

import { useHistory } from "react-router";

const RegisterComponent = () => {

 const history = useHistory();
 const [value, setValue] = useState({
   email: "",
   password: "",
   confirmPassword: "",
 });
 const [error, setError] = useState({
   email: "",
   password: "",
   confirmPassword: "",
 })

 const handleChange = (event) => {
   const { name, value } = event.target;
   setValue(prevState => ({
     ...prevState,
     [name]: value
 }));
 };

 const validate = () => {
   setError(({password: "", email: ""}));
   const emailValidate = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
   console.log(emailValidate.test(value.email));
   console.log(value)
   if (emailValidate.test(value.email) !== true) {
     setError(prevState => ({
       ...prevState,
       email: "Email is incorrect"
   }));
   }
   if (value.password.length < 6) {
     setError(prevState => ({
       ...prevState,
       password: "Password is too short"
   }));
   }
   if(value.confirmPassword !== value.password) {
     setError(prevState => ({
       ...prevState,
       confirmPassword: "Passwords are mismatched"
   }));
   }
   config
   .auth()
   .createUserWithEmailAndPassword(value.email, value.password)
   .then(() => {
     history.push("/home");
     let loggedIn = app.auth().currentUser;
     let user = {
       email: value.email,
       password: value.password,
       uid: loggedIn.uid,
       created: loggedIn.metadata.lastSignInTime,
     };
     database.collection("users").doc(user.uid).set(user);
   })
   .catch((error) => {
     setError(error.message);
   });
 }

 return (
  <section className="t-login">
   <div className="o-row o-row--login">
    <h1 className="c-heading c-heading--level1 e-alignCenter">Załóż konto</h1>
    <div className="c-icon-box__image margin__botom-top"></div>
    <form className="t-form-box" onSubmit={handleSubmit}>
     <div className="t-form-login">
      <label htmlFor="email c-heading--level5">Email</label>

      <input
       type="email"
       name="email"
       id="email"
       value={email}
       onChange={handleChange}
       className={errors.email ? 'error' : null}
      ></input>
      {errors.email}

      <br />
      <br />
      <label htmlFor="password">Hasło</label>
      <input
       type="password"
       name="password"
       id="password"
       value={password}
       onChange={handleChange}
      ></input>
      {errors.password}

      <br />
      <br />
      <label htmlFor="password2">Powtórz hasło</label>
      <input
       type="password"
       name="password2"
       id="password2"
       value={confirmPassword}
       onChange={handleChange}
      ></input>
      {password !== confirmPassword}

     </div>
     <div className="t-login__buttons">

      <input
       type="submit"
       value="Załóż konto"
       className="c-btn c-heading--level4 c-btn__login"></input>

      <NavLink to="/logowanie" className="c-btn c-heading--level4 c-btn__login c-btn__start">
       Zaloguj
      </NavLink>

     </div>
    </form>
   </div>
  </section>
 );
};

export default Register;
